-- 프로시저 ddokbun.consume_log_table_reduce 구조 내보내기
DELIMITER //
CREATE PROCEDURE `consume_log_table_reduce`(IN max  int)
BEGIN
	DECLARE c INT;
     
	SELECT cl.consume_log_seq INTO c 
	FROM consume_log cl
    LIMIT max,1;
    
    DELETE FROM consume_log
    WHERE consume_log_seq < c;
END//
DELIMITER ;

